import React, { useState, useEffect } from 'react';
import axios from 'axios';

function AdminDashboard() {
  const [rules, setRules] = useState([]);
  const [newRule, setNewRule] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchRules();
  }, []);

  const fetchRules = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/knowledge-base');
      setRules(res.data);
    } catch (err) {
      setMessage('Failed to fetch rules');
    }
  };

  const handleAddRule = async () => {
    if (!newRule.trim()) return;
    const rulesArray = newRule
      .split('\n')
      .map(rule => rule.trim())
      .filter(rule => rule.length > 0);
    if (rulesArray.length === 0) return;
    setIsLoading(true);
    try {
      await axios.post('http://localhost:5000/api/knowledge-base', { rule: rulesArray });
      setNewRule('');
      setMessage('Rules added successfully');
      fetchRules();
    } catch (err) {
      setMessage('Failed to add rules');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div style={{ maxWidth: 600, margin: 'auto', padding: 20 }}>
      <h2>Admin Dashboard - Manage Knowledge Base</h2>
      {message && <p>{message}</p>}
      <div>
      <textarea
        rows="6"
        style={{ width: '100%', padding: 10 }}
        value={newRule}
        onChange={(e) => setNewRule(e.target.value)}
        placeholder="Enter new rules, one per line, e.g. IF symptom1 AND symptom2 THEN diagnosis='disease'"
        disabled={isLoading}
      />
      <button onClick={handleAddRule} style={{ marginTop: 10 }} disabled={isLoading}>
        {isLoading ? 'Adding...' : 'Add Rules'}
      </button>
      </div>
      <h3>Existing Rules</h3>
      <ul>
        {rules.map((rule) => (
          <li key={rule._id}>{rule.rule}</li>
        ))}
      </ul>
    </div>
  );
}

export default AdminDashboard;
